import React from 'react'

export default function Table(props) {
    
    let Occupied_hours = [];
    props.data.forEach(element => {
        Occupied_hours.push({
            low_limit_hour: parseInt(element.split('-')[0].split(':')[0],10),
            low_limit_min: parseInt(element.split('-')[0].split(':')[1],10),
            high_limit_hour: parseInt(element.split('-')[1].split(':')[0],10),
            high_limit_min: parseInt(element.split('-')[1].split(':')[1],10)
        })
    });
    
    Occupied_hours.sort((a,b)=>{
        if(a.low_limit_hour>b.low_limit_hour){
            return 1
        }
        if(b.low_limit_hour>a.low_limit_hour){
            return -1
        }
        return 1

    })
    console.log(Occupied_hours)
    let selected_hour = null
    if(props.selected_hour.length > 0){
        
        selected_hour = {
            low_limit_hour: parseInt(props.selected_hour.split('-')[0].split(':')[0],10),
            low_limit_min: parseInt(props.selected_hour.split('-')[0].split(':')[1],10),
            high_limit_hour: parseInt(props.selected_hour.split('-')[1].split(':')[0],10),
            high_limit_min: parseInt(props.selected_hour.split('-')[1].split(':')[1],10)
        }
    }

    //Create table with all hours
    let init_hour = props.init_hour;
    let last_hour = props.last_hour;

    let content = [];
    let min = ''
    let i = 0;
    let classname = 'freeHour';
    let message = ''
    let locked = false;
    let func = props.handleClick;
    let displayed = true;
    let next_hour = null;
    let next_min = null;

    for (let hour = init_hour; hour < last_hour; hour++) {
         for (let minutes = 0; minutes < 60; minutes= minutes + 15) {
            if(minutes === 0){
                min = '00'
            }else{
                min = minutes
            }
            if (Occupied_hours.length>0){
                if((hour >= Occupied_hours[i].low_limit_hour)&&
                    (min >= Occupied_hours[i].low_limit_min) && !locked){
                        classname = 'occupiedHour'
                        message= 'not free'
                        locked = true;
                        func = null;
                }
                
                if((hour >= Occupied_hours[i].high_limit_hour)&&
                (min >= Occupied_hours[i].high_limit_min) && locked){
                        classname = 'freeHour'
                        message = ''
                        locked = false
                        i = i + 1;
                        func = props.handleClick;
                        if(i > Occupied_hours.length - 1){
                            i = Occupied_hours.length - 1
                            locked = true
                        }
                }
            }
            if(selected_hour && displayed){
                if((hour >= selected_hour.low_limit_hour)&&
                    (min >= selected_hour.low_limit_min) && !locked){
                        
                        classname = 'selectedHour'
                        message= 'selected'
                        locked = true;
                        func = props.handleClick
                }
                
                if((hour >= selected_hour.high_limit_hour)&&
                (min >= selected_hour.high_limit_min) && locked){
                        classname = 'freeHour'
                        message = ''
                        locked = false
                        displayed = false
                        func = props.handleClick
                }
            }

            if(min === 45){
                next_hour = hour + 1;
                next_min = '00'
            }else{
                next_min = minutes + 15
                next_hour = hour
            }

            content.push(
                <tr key={`${hour}:${min}`}>
                    <td onClick={func} id = {`${hour}:${min}`}>{`${hour}:${min}-${next_hour}:${next_min}`}</td>
                    <td onClick={func} id = {`${hour}:${min}`} className={classname}>{message}</td>
                </tr>
            )
        }
    }

    return (
        <div className="over">
            <table id="dateTable">
                <thead>
                    <tr>
                        <th>Hours</th>
                        <th className="slots">Slots</th>
                    </tr>
                </thead>
                <tbody>
                        {content}
                </tbody>
            </table>
        </div>
    )
}
